import React, { useEffect, useState } from "react";
import axios from "axios";
import io from "socket.io-client";

const socket = io("http://localhost:5000"); // or your backend URL

function App() {
  const [token, setToken] = useState("");
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [dueDate, setDueDate] = useState("");

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const t = urlParams.get("token");
    if (t) {
      localStorage.setItem("token", t);
      setToken(t);
      fetchTasks(t);
    } else {
      const stored = localStorage.getItem("token");
      if (stored) {
        setToken(stored);
        fetchTasks(stored);
      }
    }
  }, []);

  useEffect(() => {
    socket.on("taskCreated", () => {
      if (token) fetchTasks(token);
    });
    socket.on("taskUpdated", () => {
      if (token) fetchTasks(token);
    });
    socket.on("taskDeleted", () => {
      if (token) fetchTasks(token);
    });
    return () => {
      socket.off("taskCreated");
      socket.off("taskUpdated");
      socket.off("taskDeleted");
    };
  }, [token]);

  const fetchTasks = async (jwt) => {
    const res = await axios.get("http://localhost:5000/api/tasks", {
      headers: { Authorization: `Bearer ${jwt}` },
    });
    setTasks(res.data);
  };

  const addTask = async () => {
    if (!title.trim()) return;
    await axios.post(
      "http://localhost:5000/api/tasks",
      { title, dueDate },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    setTitle("");
    setDueDate("");
    // IMPORTANT: No setTasks([...tasks, newTask])
    // Just re-fetch:
    fetchTasks(token);
  };

  const updateStatus = async (id, status) => {
    await axios.put(
      `http://localhost:5000/api/tasks/${id}`,
      { status },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    fetchTasks(token);
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchTasks(token);
  };

  if (!token) {
    return (
      <div>
        <a href="http://localhost:5000/api/auth/google">Login with Google</a>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto", fontFamily: "Arial" }}>
      <h1>Your Tasks</h1>
      <div style={{ marginBottom: "10px" }}>
        <input
          placeholder="Task title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>
      <ul>
        {tasks.map((task) => (
          <li key={task._id} style={{ marginBottom: "5px" }}>
            <strong>{task.title}</strong> - {task.status}
            {task.dueDate && ` (Due: ${new Date(task.dueDate).toLocaleDateString()})`}
            <button
              onClick={() =>
                updateStatus(
                  task._id,
                  task.status === "completed" ? "in progress" : "completed"
                )
              }
              style={{ marginLeft: "10px" }}
            >
              {task.status === "completed" ? "Mark In Progress" : "Mark Completed"}
            </button>
            <button
              onClick={() => deleteTask(task._id)}
              style={{ marginLeft: "5px" }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
